function H = homographie(Points)  j = 1;  M = zeros(2*size(Points,1),9);  for i = 1 : size(Points,1)    x = Points(i,1);    y = Points(i,2);    X = Points(i,3);    Y = Points(i,4);    rx = [[x y 1] [0 0 0] [-X*x -X*y -X]];    ry = [[0 0 0] [x y 1] [-Y*x -Y*y -Y]];       M(j,:) = rx;    M(j+1,:) = ry;    j=j+2;  end    [U,S,V] = svd(M);  V0 = transpose(V(:,end));  %matrice de l'homographie  H = [ [V0(1:3)] ; [V0(4:6)] ; [V0(7:9)] ];endreturn